<!-- En ExportToPDFButton.vue -->
<template>
  <div>
    <q-btn @click="exportToPDF" label="Exportar a PDF" color="positive" />
  </div>
</template>

<script>
import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";

pdfMake.vfs = pdfFonts.pdfMake.vfs;

export default {
  props: {
    data: {
      type: Array,
      required: true,
    },
  },
  methods: {
    exportToPDF() {
      if (Array.isArray(this.data)) {
        const content = this.data.map((row) => [
          { text: row.label, style: "tableHeader" },
          { text: row.value, style: "tableData" },
        ]);

        const docDefinition = {
          header: {
            columns: [
            {
                image: 'data:image/png;base64,', // Ruta local a la imagen descargada
                alignment: "left",
                width: 150,
                margin: [10, 5, 0, 0]
              },
              {
                text: "Proceso de selecci√≥n \n Octubre 2023",
                alignment: "center",
                fontSize: 12,
                bold: true,
                margin: [250, 10, 0, 0 ]
              }
            ],
          },
          content: [
            { text: "", margin: [0, 80, 0, 0] }, // Espacio entre el header y la tabla
            {
              table: {
                headerRows: 2,
                fillColor: "black",
                widths: ["*", "*"],
                
                body: [
                  [
                    { text: "Field Label", style: "tableHeader" },
                    { text: "Data Value", style: "tableHeader" },
                  ],
                  ...content,
                ],
              },
            },
          ],
          footer: {
            columns: [
              "Jordi Velasco",
              {
                text: `Fecha: ${new Date().toLocaleDateString()}`, // Muestra la fecha actual
                alignment: "right",
                
              },
            ],
            margin: [10, 10, 10, 10],
          },
          styles: {
            tableHeader: {
              bold: true,
              fontSize: 13,
              color: "black",
            },
            tableData: {
              fontSize: 12,
              color: "black",
            },
          },
        };

        pdfMake.createPdf(docDefinition).open();
      } else {
        console.error('El prop "data" no es un array:', this.data);
      }
    },
  },
};
</script>
